```
var achievement = [
  {name: '小米', score: 90},
  {name: '小红', score: 82},
  {name: '小黑', score: 60},
  {name: '小白', score: 92},
  {name: '小绿', score: 85},
  {name: '小黄', score: 100}
]

var objectArraySort = function (keyName) {
 return function (objectN, objectM) {
  var valueN = objectN[keyName]
  var valueM = objectM[keyName]
  if (valueN < valueM) return 1
  else if (valueN > valueM) return -1
  else return 0
 }
}

function ranking (achievement, num) {
  var rankingArr = achievement.sort(objectArraySort('score'))
  var newRankingArr = [];
  for (var i=0; i<num; i++) {
     newRankingArr.push(rankingArr[i].name)
  }
  return newRankingArr
}

ranking(achievement, 3) // ['小黄', '小白', '小米']

```
