var get = function (url, key) {
	var newUrl = url.substr(url.lastIndexOf('?')+1)
    var queryArr = newUrl.split('&')
    var queryObj = {};
    queryArr.forEach((item) => {
      var queryItem = item.split('=')
      queryObj[queryItem[0]] = queryItem[1]
    })
    return queryObj[key]
}
