function check(str){
            var U=0,D=0,L=0,R=0;

            for(var i=0;i<str.length;i++){
                switch (str[i]){
                    case "U": U++;break;
                    case "D": D++;break;
                    case "L": L++;break;
                    case "R": R++;break;
                }

            }
            return (U===D && L===R) 
        }
