function search(arr,item){
  let low = 0;
  let heigh = arr.length - 1;
  while(low <= heigh){
    let mid = (low + heigh) / 2;
    mid = Math.ceil(mid);
    if(arr[mid] === item){
      return mid;
    }
    if(arr[mid] < item){
      low = mid + 1;
    }
    if(arr[mid] > item){
      heigh = mid - 1;
    }
  }
}
